import React from "react";
import { useState } from "react";
import Note from "./Note";
const CreateNote = (props) => {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  const inputEvent = () => {
    setNote((prevNotes) => {
      return {};
    });
  };

  return (
    <>
      <div>
        <br />
        <input
          type="text"
          value={note.title}
          onChange={inputEvent}
          placeholder="Title"
        />
        <br />
        <textarea
          value={note.content}
          onChange={inputEvent}
          placeholder="Write a note..."
          rows="4"
          cols="21"
        />
        <button>+</button>
      </div>
    </>
  );
};

export default CreateNote;
