import React from "react";
import { useEffect } from "react";
import { useState } from "react";

const App = () => {
  const [num, setNum] = useState(0);
  const [num1, setNum1] = useState(0);
  useEffect(() => {
    alert("I'm Clicked");
  }, [num]);
  //Here it will apply on only on first button , because here we gave num;

  return (
    <>
      <button
        onClick={() => {
          setNum(num + 1);
        }}
      >
        Click Me {num}
      </button>
      <button
        onClick={() => {
          setNum1(num1 + 1);
        }}
      >
        Click Me {num1}
      </button>
    </>
  );
};

export default App;
